sys_script_client.script:
function onLoad() {

	if (!g_form.isNewRecord()){
		return;
	}
   //Type appropriate comment here, and begin script below
var date = new Date();
	var monthIndex = date.getMonth()+1;
    if(monthIndex<10){
        monthIndex=('0'+monthIndex);
	}
	var current_date = date.getFullYear()+"-"+(monthIndex)+"-"+ date.getDate();
	var seconds = date.getSeconds();
    if(seconds<10){
        seconds=('0'+seconds);
	}
	var current_time = date.getHours()+":"+date.getMinutes()+":"+ seconds;
	//alert(current_date + ' ' + current_time);

	var now = current_date + ' ' + current_time;

	g_form.setValue('u_when_needed',now);
	//var whenneeded = g_form.getValue('u_when_needed');

}
